CREATE TABLE IF NOT EXISTS public.scheduled_trips
(
    trip_id serial PRIMARY KEY ,
    email_address character varying(125) COLLATE pg_catalog."default" NOT NULL,
    departure_latitude character varying(255) COLLATE pg_catalog."default" NOT NULL,
    departure_longitude character varying(255) COLLATE pg_catalog."default" NOT NULL,
    destination_latitude character varying(255) COLLATE pg_catalog."default" NOT NULL,
    destination_longitude character varying(255) COLLATE pg_catalog."default" NOT NULL,
    departure_date timestamp with time zone NOT NULL,
    mobile_number character varying(125) COLLATE pg_catalog."default",
    mobile_provider character varying(125) COLLATE pg_catalog."default",
    user_id_discord character varying(125) COLLATE pg_catalog."default",
    user_id_slack character varying(125) COLLATE pg_catalog."default",
    created_at timestamp with time zone NOT NULL DEFAULT CURRENT_TIMESTAMP,
    notification_status character varying(125) COLLATE pg_catalog."default"
)


